import { ItemCategoryEnum } from "@/enums/ItemCategoryEnum";

export default class ItemModel {
  id: number;

  nome: string;

  descricao: string;

  preco: number;

  _base64 =
    "data:image/jpeg;base64,";

  categoria = ItemCategoryEnum.TODOS;

  constructor(
    id: number,
    nome: string,
    descricao: string,
    preco: number,
    categoria: ItemCategoryEnum
  ) {
    this.id = id;
    this.nome = nome;
    this.descricao = descricao;
    this.preco = preco;
    this.categoria = categoria;
  }

  get base64(): string {
    if (this._base64 != null && this._base64.length > 0) {
      return this._base64;
    } else {
      return "data:image/jpeg;base64,";
    }
  }
}
